// ğŸ”¹ FunciÃ³n auxiliar: convierte "10:00am" â†’ { hour: 10, minute: 0 } en 24h
function parseHourTo24(horaTexto) {
  if (!horaTexto) return { hour: 9, minute: 0 }; // fallback 09:00

  let h = horaTexto.trim().toLowerCase();
  h = h.replace(/\s+/g, "");      // "10:00 am" -> "10:00am"



  const isPM = h.includes("pm");
  const isAM = h.includes("am");
  h = h.replace(/am|pm/g, "");

  let [hourStr, minStr] = h.split(":");
  let hour = parseInt(hourStr || "0", 10);
  let minute = parseInt(minStr || "0", 10);

  if (isPM && hour < 12) hour += 12;
  if (isAM && hour === 12) hour = 0;

  if (!Number.isFinite(hour) || !Number.isFinite(minute)) {
    hour = 9;
    minute = 0;
  }

  return { hour, minute };
}

