// ---- Helpers ----
function parseToMinutes(str) {
  if (!str) return null;

  str = String(str)
    .toLowerCase()
    .replace(/\s+/g, "");       // "1:00 pm" -> "1:00pm"

  const m = str.match(/(\d{1,2})(?::?(\d{2}))?(am|pm)?/);
  if (!m) return null;

  let h = parseInt(m[1], 10);
  let min = m[2] ? parseInt(m[2], 10) : 0;
  let ampm = m[3];

  if (!ampm) {
    if (h === 0) {
      ampm = "am"; h = 12;
    } else if (h === 12) {
      ampm = "pm";
    } else if (h > 12) {
      ampm = "pm"; h = h - 12;
    } else {
      ampm = "am";
    }
  }

  let h24 = h;
  if (ampm === "pm" && h !== 12) h24 = h + 12;
  if (ampm === "am" && h === 12) h24 = 0;

  return h24 * 60 + min;
}

function minutesToCanonical(minutes) {
  if (minutes == null) return "";

  let h24 = Math.floor(minutes / 60);
  const min = minutes % 60;

  const ampm = h24 >= 12 ? "pm" : "am";
  let h12 = h24 % 12;
  if (h12 === 0) h12 = 12;

  const hh = String(h12).padStart(2, "0");
  const mm = String(min).padStart(2, "0");

  return `${hh}:${mm} ${ampm}`;
}

